css:
h2 {
    color: #e65023;
    border-bottom: 2px solid #e65023;
    /* text-transform: uppercase; */
    font-style: italic;
    font-weight: 700;
    font-size: 14px;
    margin-top: 15px;
    margin-bottom: 5px;
}

h3 {
    font-size: 12px;
    margin-top: 10px;
    margin-bottom: 5px;
}

h4 {
    font-size: 12px;
    font-style: italic;
    font-weight: normal;
    margin-top: 10px;
    margin-bottom: 2px;
}

hr {
    width: 100%;
    border-top: 1px solid silver;
}

#header {
    height: 65px !important;
    width: 100% !important;
}

.tacenter {
    text-align: center;
}

.taright {
    text-align: right;
}

.bold {
    font-weight: bold;
}

.period {
    text-align: left;
}

.amount {
    text-align: right;
}

.btop {
    border-top: 1px solid silver;
}

.bbot {
    border-bottom: 1px solid silver;
}

.fleft {
    float: left;
}

.fright {
    float: right;
}

.print-format td {
    padding-left: 0px !important;
}

#head {
    width: 100%;
    font-size: 16px;
}

#meta {
    width: 100%; 
    border-top: 1px solid silver;
    border-bottom: 1px solid silver;
}

#meta td {
    padding: 0px !important;
    padding-top: 2px !important;
    padding-bottom: 1px !important;
}

#sample-meta {
    width: 100%; 
    border-top: 1px solid silver;
    border-bottom: 1px solid silver;
}

#sample-meta td {
    padding: 0px !important;
    padding-top: 2px !important;
    padding-bottom: 1px !important;
}

#sample-result {
    width: 100%; 
    border-top: 0px;
    border-bottom: 0px;
    background-color: #f4f4f4;
}

#sample-result td {
    padding-top: 2px !important;
    padding-bottom: 2px !important;
}

#subject {
    font-size: 16px !important;
    margin-top: 10px !important; 
    margin-bottom: 10px !important
}
